import React from "react";
import { useSelector } from "react-redux";
import { getAllMovies } from "../features/getMovieSlice";
import { getFav } from "../features/favouritesSlice";
import AddFavourite from "./AddFavourites";
import RemoveFavourite from "./RemoveFavourites"

const MovieList = () => {
  const movies = useSelector(getAllMovies);
  const favs = useSelector(getFav);
  return (
    <>
      {movies.map((movie, index) => (
        <div
          key={index}
          className="m-2 image-container"
          style={{ width: "30%" }}
        >
          {/* <div className="card-body mb-5" ></div> */}

          <p
            className="mb-1"
            // style={{ width: "20rem", height: "2rem" }}
          >
            {movie.Title}
          </p>
          <div
            className="image-container"
            // style={{ width: "20rem" }}
          >
            <img
              src={movie.Poster}
              className=""
              alt={movie.Title}
              // img-fluid
              // style={{ height: "20%" }}
            ></img>

            <br></br>
            {console.log(favs.includes(movie) === true)}
            {/* {if (favs.includes(movie) == true){
              <RemoveFavourite><RemoveFavourite/>
            }} */}
            <AddFavourite hide="true"
              movieData={movie}
            ></AddFavourite>
            {/* <span
              className="overlay"
              style={{}}
            >
              Favorite {index}.
            </span> */}
          </div>
        </div>
      ))}
    </>
  );
};
export default MovieList;

