  const movies = useSelector(getAllMovies);
  return (
    <>
      {movies.map((movie, index) => (
        <div key={index} className="m-2" style={{ width: "30rem" }}>
          {/* <div className="card-body mb-5" ></div> */}

          <p
            className=""
            // style={{ width: "20rem", height: "2rem" }}
