        <MovieListHeading heading="Movies" />
        <br></br>
      </div>
      <div className="row d-flex algin-items-center mt-4 mb-4">
        <Search  />
      </div>
      <div className="d-flex  mr-5">
        <MovieList movies={movies} />
      </div>
    </div>
  );
}
