import React from "react";
import { useSelector } from "react-redux";
import { getFav } from "../features/favouritesSlice";

const FavList = () => {
  const favs = useSelector(getFav);
  console.log(favs)
  return (
    <>
    <h2>
        Favs
    </h2>
    <br></br>
      {favs.map((fav, index) => (
        <div key={index} className="m-2 image-container" style={{ width: "30%" }}>
          {/* <div className="card-body mb-5" ></div> */}

          <p
            className="mb-1"
            // style={{ width: "20rem", height: "2rem" }}
          >
            {fav.title}
          </p>
          <div
            className="image-container"
            // style={{ width: "20rem" }}
          >
            <img
              src={fav.poster}
              className=""
              alt={fav.title}
              // img-fluid
              // style={{ height: "20%" }}
            ></img>

            <br></br>
            {/* <AddFavourite title={movie.Title} poster ={movie.Poster}></AddFavourite> */}
            {/* <span
              className="overlay"
              style={{}}
            >
              Favorite {index}.
            </span> */}
          </div>
        </div>
      ))}
    </>
  );
};
export default FavList;
