import { createAsyncThunk, createSlice } from "@reduxjs/toolkit";

const initialState = {
  fav: [],
};

const favSlice = createSlice({
  name: "favourites",
  initialState,
  reducers: {
    addFav: (state, action) => {
      state.favourites = [...state.favourites, action.payload] (prevState => prevState + action.payload )
    },
  },
});

export const { addFav } = favSlice.actions;
export const getFav = (state) => state.favourites.favourites;
export default favSlice.reducer;
